import { executeQuery } from './neo4jConnection';

export function checkHypothesis(suspect, weapon, room) {
    const query = `
        MATCH (s:Person {name: '${suspect}'})-[:MYSTERY]->(w:Weapon {name: '${weapon}'}), 
              (w)-[:MYSTERY]->(r:Room {name: '${room}'})
        RETURN s, w, r`;

    return executeQuery(query).then(result => result.records.length > 0);
}
