// src/Board.js
import React from 'react';
import './Board.css'; // Assure-toi de créer ce fichier CSS pour le style

// Import des illustrations des salles
import cuisineImage from '../img/cuisine.jpg';
import verandaImage from '../img/veranda.jpg';
import grandSalonImage from '../img/grand_salon.jpg';
import petitSalonImage from '../img/petit_salon.jpg';
import studioImage from '../img/studio.jpg';
import hallImage from '../img/hall.jpg';
import bibliothequeImage from '../img/bibliotheque.jpg';
import salleAMangerImage from '../img/salle_a_manger.jpg';
import bureauImage from '../img/bureau.jpg';

const roomImages = {
  'Cuisine': '/img/cuisine.jpg',
  'Grand Salon': '/img/grand_salon.jpg',
  'Veranda': '/img/veranda.jpg',
  'Petit Salon': '/img/petit_salon.jpg',
  'Studio': '/img/studio.jpg',
  'Hall': '/img/hall.jpg',
  'Bibliotheque': '/img/bibliotheque.jpg',
  'Salle à manger': '/img/salle_a_manger.jpg',
  'Bureau': '/img/bureau.jpg',
};


const Board = ({ roomsState }) => {
  return (
    <div className="board">
      {roomsState.map((room, index) => (
        <div key={index} className="room">
          <h3>{room.name}</h3>
          <img 
            src={roomImages[room.name] || 'default.jpg'} 
            alt={room.name} 
            className="room-image" 
          />
          {room.occupants.length > 0 ? (
            room.occupants.map((person, idx) => (
              <p key={idx}>
                {person.name} ({person.color})
              </p>
            ))
          ) : (
            <p>Aucun occupant</p>
          )}
        </div>
      ))}
    </div>
  );
};

export default Board;
