// src/Board.js
import React from 'react';
import './Board.css'; 

const roomImages = {
  'Cuisine': 'https://via.placeholder.com/150?text=Cuisine',
  'Grand Salon': 'https://via.placeholder.com/150?text=Grand+Salon',
  'Véranda': 'https://via.placeholder.com/150?text=Véranda',
  'Petit Salon': 'https://via.placeholder.com/150?text=Petit+Salon',
  'Studio': 'https://via.placeholder.com/150?text=Studio',
  'Hall': 'https://via.placeholder.com/150?text=Hall',
  'Bibliothèque': 'https://via.placeholder.com/150?text=Bibliothèque',
  'Salle à manger': 'https://via.placeholder.com/150?text=Salle+à+manger',
  'Bureau': 'https://via.placeholder.com/150?text=Bureau',
};

const Board = ({ roomsState = [] }) => {
  return (
    <div className="board">
      {roomsState.length > 0 ? (
        roomsState.map((room, index) => (
          <div key={index} className="room">
            <h3>{room.name}</h3>
            <img 
              src={roomImages[room.name] || '/img/default.jpg'} 
              alt={room.name} 
              className="room-image" 
              onError={(e) => {
                e.target.onerror = null; // Évite les boucles d'erreur
                e.target.src = '/img/default.jpg'; // Image par défaut
              }}
            />
          </div>
        ))
      ) : (
        <p>Le plateau est vide.</p>
      )}
    </div>
  );
};

export default Board;
