// src/Board.js
import React from 'react';
import './Board.css'; // Assure-toi de créer ce fichier CSS pour le style

// Dictionnaire des images associées à chaque salle
const roomImages = {
  'Cuisine': '/img/cuisine.jpg',
  'Grand Salon': '/img/grand_salon.jpg',
  'Véranda': '/img/veranda.jpg', // Corrigé l'accent
  'Petit Salon': '/img/petit_salon.jpg',
  'Studio': '/img/studio.jpg',
  'Hall': '/img/hall.jpg',
  'Bibliothèque': '/img/bibliotheque.jpg', // Corrigé l'accent
  'Salle à manger': '/img/salle_a_manger.jpg',
  'Bureau': '/img/bureau.jpg',
};

// Composant Board qui affiche les salles et leurs images
const Board = ({ roomsState = [] }) => {
  return (
    <div className="board">
      {roomsState.length > 0 ? (
        roomsState.map((room, index) => (
          <div key={index} className="room">
            <h3>{room.name}</h3>
            <img 
              src={roomImages[room.name] || '/img/default.jpg'} 
              alt={room.name} 
              className="room-image" 
              onError={(e) => {
                e.target.onerror = null; // Empêche une boucle d'erreur
                e.target.src = '/img/default.jpg'; // Image par défaut si l'image n'est pas trouvée
              }}
            />
            {room.occupants.length > 0 ? (
              room.occupants.map((person, idx) => (
                <p key={idx}>
                  {person.name} ({person.color})
                </p>
              ))
            ) : (
              <p>Aucun occupant</p>
            )}
          </div>
        ))
      ) : (
        <p>Le plateau est vide.</p>
      )}
    </div>
  );
};

export default Board;
