import { executeQuery } from './neo4jConnection';

export async function getInitialState() {
    try {
        const result = await executeQuery('MATCH (r:Room) RETURN r');
        console.log('Données récupérées:', result);
        if (!result || !Array.isArray(result)) {
            throw new Error('Format de données inattendu');
        }
        return result.map(record => ({
            name: record.r ? record.r.properties.name : '', // Retourner une chaîne vide si le nom n'est pas défini
            occupants: [] // Adaptez cette partie selon votre modèle de données
        }));
    } catch (error) {
        console.error('Erreur lors de la récupération des données:', error);
        return []; // Retournez un tableau vide en cas d'erreur
    }
}

const checkHypothesis = async (suspect, weapon, room) => {
    try {
        const result = await executeQuery(`
            MATCH (s:Suspect {name: '${suspect}'}), (w:Weapon {name: '${weapon}'}), (r:Room {name: '${room}'})
            RETURN s, w, r
        `);
        
        // Vérifiez si le résultat contient les données attendues
        if (result.length > 0) {
            // Traitez la vérification de l'hypothèse ici
            const isCorrect = {
                suspect: suspect === crime.suspect,
                weapon: weapon === crime.weapon,
                room: room === crime.room
            };
            
            if (isCorrect.suspect) {
                alert('Suspect trouvé !');
            }
            if (isCorrect.weapon) {
                alert('Arme trouvée !');
            }
            if (isCorrect.room) {
                alert('Salle correcte !');
            }

            // Vérifiez si la combinaison complète est correcte
            if (isCorrect.suspect && isCorrect.weapon && isCorrect.room) {
                alert('Vous avez gagné ! Vous avez trouvé le crime !');
                onGameOver();
            }
        }
    } catch (error) {
        console.error('Erreur lors de la vérification de l\'hypothèse:', error);
    }
};
