// src/Board.js
import React from 'react';
import './Board.css'; 

// Dictionnaire des images associées à chaque salle
const roomImages = {
  'Cuisine': '/img/cuisine.jpg',
  'Grand Salon': '/img/grand_salon.jpg',
  'Véranda': '/img/veranda.jpg',
  'Petit Salon': '/img/petit_salon.jpg',
  'Studio': '/img/studio.jpg',
  'Hall': '/img/hall.jpg',
  'Bibliothèque': '/img/bibliotheque.jpg',
  'Salle à manger': '/img/salle_a_manger.jpg',
  'Bureau': '/img/bureau.jpg',
};

const Board = ({ roomsState = [] }) => {
  console.log('roomsState dans Board:', roomsState);

  // Vérifie si des salles sont présentes
  if (roomsState.length === 0) {
    return <div>Aucune salle à afficher.</div>;
  }

  return (
    <div className="board">
      {roomsState.map((room, index) => {
        console.log('Nom de la salle:', room.name); // Ajout du log
        return (
          <div key={index} className="room">
            <img 
              src={roomImages[room.name]} 
              alt={room.name} 
              className="room-image" 
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = ''; // Pas d'image par défaut
              }}
            />
            <h3>{room.name || 'Salle inconnue'}</h3>
          </div>
        );
      })}
    </div>
  );
};

export default Board;
