// src/gameLogic.js
import { executeQuery } from './neo4jConnection';

export async function getInitialState() {
    try {
        const result = await executeQuery('MATCH (r:Room) RETURN r');
        console.log('Données récupérées:', result); // Ajoutez cette ligne pour voir les données dans la console
        return result.map(record => ({
            name: record.r.properties.name,
            occupants: [] // Adaptez cette partie selon votre modèle de données
        }));
    } catch (error) {
        console.error('Erreur lors de la récupération des données:', error);
        return []; // Retournez un tableau vide en cas d'erreur
    }
}

export async function checkHypothesis(suspect, weapon, room) {
    try {
        const result = await executeQuery(
            `MATCH (s:Suspect {name: $suspect}), (w:Weapon {name: $weapon}), (r:Room {name: $room})
             RETURN s, w, r`,
            { suspect, weapon, room }
        );
        console.log('Résultat de l\'hypothèse:', result); // Ajoutez cette ligne pour voir les données dans la console
        return result.length > 0; // Assurez-vous que cela correspond à la structure des résultats
    } catch (error) {
        console.error('Erreur lors de la vérification de l\'hypothèse:', error);
        return false; // Retournez false en cas d'erreur
    }
}
