// src/Board.js
import React from 'react';
import './Board.css'; // Assure-toi de créer ce fichier CSS pour le style

const rooms = [
  'Cuisine', 'Grand Salon', 'Petit Salon', 'Salle a manger', 
  'Bureau', 'Bibliotheque', 'Veranda', 'Hall', 'Studio'
];

const Board = ({ onSelectRoom }) => {
  return (
    <div className="board">
      {rooms.map((room, index) => (
        <div 
          key={index} 
          className="room"
          onClick={() => onSelectRoom(room)}
        >
          {room}
        </div>
      ))}
    </div>
  );
};

export default Board;
