import React, { useState, useEffect } from 'react';
import { Container, Button, Form } from 'react-bootstrap';
import { checkHypothesis, getInitialState } from './gameLogic';
import './App.css';
import Board from './Board';

function App() {
  const [suspect, setSuspect] = useState('');
  const [weapon, setWeapon] = useState('');
  const [room, setRoom] = useState('');
  const [result, setResult] = useState('');
  const [roomsState, setRoomsState] = useState([]);
  const [playerPosition, setPlayerPosition] = useState('');
  const [aiPositions, setAiPositions] = useState([
    { name: 'IA 1', room: 'Cuisine' },
    { name: 'IA 2', room: 'Hall' },
    { name: 'IA 3', room: 'Bureau' },
  ]);

  useEffect(() => {
    getInitialState().then((data) => {
      console.log('Données initiales:', data);
      if (Array.isArray(data)) {
        setRoomsState(data);
      } else {
        console.error('Les données reçues ne sont pas au format attendu:', data);
      }
    }).catch(error => {
      console.error('Erreur lors de la récupération des données:', error);
    });
  }, []);

  const handleCheckHypothesis = () => {
    checkHypothesis(suspect, weapon, room).then(isCorrect => {
      setResult(isCorrect ? "Hypothèse correcte !" : "Hypothèse incorrecte.");
    }).catch(error => {
      console.error('Erreur lors de la vérification de l\'hypothèse:', error);
    });
  };

  return (
    <Container>
      <h1 className="text-center my-4">Cluedo Game</h1>

      {/* Intégrer le plateau */}
      <Board roomsState={roomsState} playerPosition={playerPosition} setPlayerPosition={setPlayerPosition} />

      {/* Afficher les positions des IA */}
      <div className="mt-4">
        <h2>Positions des IA :</h2>
        <ul>
          {aiPositions.map((ai, index) => (
            <li key={index}>{ai.name} est dans la {ai.room}</li>
          ))}
        </ul>
      </div>

      {/* Formulaire pour vérifier une hypothèse */}
      <Form>
        <Form.Group>
          <Form.Control
            type="text"
            placeholder="Entrez un suspect"
            value={suspect}
            onChange={(e) => setSuspect(e.target.value)}
          />
        </Form.Group>

        <Form.Group>
          <Form.Control
            type="text"
            placeholder="Entrez une arme"
            value={weapon}
            onChange={(e) => setWeapon(e.target.value)}
          />
        </Form.Group>

        <Form.Group>
          <Form.Control
            type="text"
            placeholder="Entrez une salle"
            value={room}
            onChange={(e) => setRoom(e.target.value)}
          />
        </Form.Group>

        <Button variant="primary" onClick={handleCheckHypothesis}>
          Vérifier l'hypothèse
        </Button>
      </Form>

      <p className="mt-3">{result}</p>
    </Container>
  );
}

export default App;
