// src/neo4jConnection.js
const url = 'http://localhost:7474/db/neo4j/tx/commit'; // Assurez-vous que cette URL est correcte pour votre configuration Neo4j
const user = 'neo4j';
const password = 'lecluedo';

// src/neo4jConnection.js
export function executeQuery(query) {
    return fetch(url, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Basic ' + btoa(user + ':' + password)
        },
        body: JSON.stringify({
            statements: [
                {
                    statement: query
                }
            ]
        })
    })
    .then(response => {
        if (!response.ok) {
            throw new Error('Network response was not ok');
        }
        return response.json();
    })
    .then(data => data.results[0]?.data || []) // Utilisez un accès sécurisé
    .catch(error => {
        console.error('Error executing query:', error);
        throw error; // Laissez l'erreur remonter
    });
}


export function closeConnection() {
    // Pas besoin de fermer la connexion HTTP
}
