import React from 'react';
import './Board.css'; 

// Utiliser require pour les images
const roomImages = {
  'Cuisine': require('../public/img/cuisine.jpg').default,
  'Grand Salon': require('../public/img/grand_salon.jpg').default,
  'Véranda': require('../public/img/veranda.jpg').default,
  'Petit Salon': require('../public/img/petit_salon.jpg').default,
  'Studio': require('../public/img/studio.jpg').default,
  'Hall': require('../public/img/hall.jpg').default,
  'Bibliothèque': require('../public/img/bibliotheque.jpg').default,
  'Salle à manger': require('../public/img/salle_a_manger.jpg').default,
  'Bureau': require('../public/img/bureau.jpg').default,
};

const Board = ({ roomsState = [] }) => {
  return (
    <div className="board">
      {roomsState.length > 0 ? (
        roomsState.map((room, index) => (
          <div key={index} className="room">
            <h3>{room.name}</h3>
            <img 
              src={roomImages[room.name] || '/img/default.jpg'} 
              alt={room.name} 
              className="room-image" 
              onError={(e) => {
                e.target.onerror = null; 
                e.target.src = '/img/default.jpg'; 
              }}
            />
          </div>
        ))
      ) : (
        <p>Le plateau est vide.</p>
      )}
    </div>
  );
};

export default Board;
