import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Form, Card } from 'react-bootstrap';
import { checkHypothesis, getInitialState } from './gameLogic';
import './App.css';

function App() {
    const [suspect, setSuspect] = useState('');
    const [weapon, setWeapon] = useState('');
    const [room, setRoom] = useState('');
    const [result, setResult] = useState('');
    const [roomsState, setRoomsState] = useState([]); // État du plateau (lieux et suspects)

    // Charger l'état initial du plateau (les suspects dans les lieux de départ)
    useEffect(() => {
        getInitialState().then((data) => setRoomsState(data));
    }, []);

    const handleCheckHypothesis = () => {
        checkHypothesis(suspect, weapon, room).then(isCorrect => {
            setResult(isCorrect ? "Hypothèse correcte !" : "Hypothèse incorrecte.");
        });
    };

    return (
        <Container>
            <h1 className="text-center my-4">Cluedo Game</h1>

            {/* Affichage du plateau de jeu */}
            <Row>
                {roomsState.map((room) => (
                    <Col key={room.name} md={4} className="mb-4">
                        <Card>
                            <Card.Body>
                                <Card.Title>{room.name}</Card.Title>
                                <Card.Text>
                                    {room.occupants.length > 0 ? (
                                        room.occupants.map((person) => (
                                            <div key={person.name}>
                                                {person.name} ({person.color})
                                            </div>
                                        ))
                                    ) : (
                                        <div>Aucun occupant</div>
                                    )}
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>

            {/* Formulaire pour vérifier une hypothèse */}
            <Form>
                <Form.Group>
                    <Form.Label>Suspect</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Entrez un suspect"
                        value={suspect}
                        onChange={(e) => setSuspect(e.target.value)}
                    />
                </Form.Group>

                <Form.Group>
                    <Form.Label>Arme</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Entrez une arme"
                        value={weapon}
                        onChange={(e) => setWeapon(e.target.value)}
                    />
                </Form.Group>

                <Form.Group>
                    <Form.Label>Salle</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Entrez une salle"
                        value={room}
                        onChange={(e) => setRoom(e.target.value)}
                    />
                </Form.Group>

                <Button variant="primary" onClick={handleCheckHypothesis}>
                    Vérifier l'hypothèse
                </Button>
            </Form>

            <p className="mt-3">{result}</p>
        </Container>
    );
}

export default App;
