import React, { useState } from 'react';
import { checkHypothesis } from './gameLogic';

function App() {
    const [suspect, setSuspect] = useState('');
    const [weapon, setWeapon] = useState('');
    const [room, setRoom] = useState('');
    const [result, setResult] = useState('');

    const handleCheckHypothesis = () => {
        checkHypothesis(suspect, weapon, room).then(isCorrect => {
            setResult(isCorrect ? "Hypothèse correcte !" : "Hypothèse incorrecte.");
        });
    };

    return (
        <div>
            <h1>Cluedo Game</h1>
            <input
                type="text"
                placeholder="Suspect"
                value={suspect}
                onChange={(e) => setSuspect(e.target.value)}
            />
            <input
                type="text"
                placeholder="Arme"
                value={weapon}
                onChange={(e) => setWeapon(e.target.value)}
            />
            <input
                type="text"
                placeholder="Salle"
                value={room}
                onChange={(e) => setRoom(e.target.value)}
            />
            <button onClick={handleCheckHypothesis}>Vérifier l'hypothèse</button>
            <p>{result}</p>
        </div>
    );
}

export default App;
