import React, { useState, useEffect } from 'react';
import { Container, Button, Form } from 'react-bootstrap';
import { checkHypothesis, getInitialState } from './gameLogic';
import './App.css';
import Board from './Board';

function App() {
  const [suspect, setSuspect] = useState('');
  const [weapon, setWeapon] = useState('');
  const [result, setResult] = useState('');
  const [roomsState, setRoomsState] = useState([]);
  const [playerPosition, setPlayerPosition] = useState('');
  const [aiPositions, setAiPositions] = useState([]);

  useEffect(() => {
    const initializeGame = async () => {
      const initialState = await getInitialState(); // Récupération de l'état initial
      if (initialState) {
        setRoomsState(initialState);
        initializeGame(initialState); // Initialisation du jeu avec les salles récupérées
      }
    };
    initializeGame(); // Appel de la fonction d'initialisation
  }, []);

  const initializeGame = (rooms) => {
    const allRooms = rooms.map(room => room.name);
    const randomPlayerRoom = getRandomRoom(allRooms);
    const availableRooms = allRooms.filter(room => room !== randomPlayerRoom);

    const initialAiPositions = [
      { name: 'IA 1', room: getRandomRoom(availableRooms) },
      { name: 'IA 2', room: getRandomRoom(availableRooms.filter(r => r !== initialAiPositions[0].room)) },
      { name: 'IA 3', room: getRandomRoom(availableRooms.filter(r => r !== initialAiPositions[0].room && r !== initialAiPositions[1].room)) }
    ];

    setPlayerPosition(randomPlayerRoom);
    setAiPositions(initialAiPositions);
  };

  const getRandomRoom = (rooms) => {
    return rooms[Math.floor(Math.random() * rooms.length)];
  };

  useEffect(() => {
    if (playerPosition) {
      moveAI();
    }
  }, [playerPosition]);

  const handleMovePlayer = (newRoom) => {
    setPlayerPosition(newRoom);
    moveAI();
  };

  const moveAI = () => {
    const updatedAiPositions = aiPositions.map(ai => {
      const accessibleRooms = getAccessibleRooms(ai.room);
      return {
        ...ai,
        room: accessibleRooms.length > 0 ? getRandomRoom(accessibleRooms) : ai.room
      };
    });
    setAiPositions(updatedAiPositions);
  };

  const getAccessibleRooms = (currentRoom) => {
    const roomMap = {
      'Cuisine': ['Grand Salon', 'Salle à manger'],
      'Grand Salon': ['Cuisine', 'Véranda', 'Salle à manger'],
      'Véranda': ['Grand Salon', 'Petit Salon'],
      'Petit Salon': ['Véranda', 'Hall', 'Bibliothèque'],
      'Studio': ['Hall'],
      'Hall': ['Cuisine', 'Grand Salon', 'Petit Salon', 'Studio', 'Bureau'],
      'Bibliothèque': ['Petit Salon'],
      'Salle à manger': ['Grand Salon'],
      'Bureau': ['Hall']
    };
    return roomMap[currentRoom] || [];
  };

  const handleCheckHypothesis = () => {
    checkHypothesis(suspect, weapon, playerPosition, (gameOver) => {
      setResult(gameOver ? "Vous avez gagné !" : "Essayez encore !");
    });
  };

  return (
    <Container style={{ position: 'relative', display: 'flex' }}>
      <div style={{ flexGrow: 1 }}>
        <h1 className="text-center my-4">Cluedo Game</h1>
        <Board 
          roomsState={roomsState} 
          playerPosition={playerPosition} 
          setPlayerPosition={handleMovePlayer} 
          aiPositions={aiPositions}
        />

        <Form>
          <Form.Group>
            <Form.Control
              type="text"
              placeholder="Entrez un suspect"
              value={suspect}
              onChange={(e) => setSuspect(e.target.value)}
            />
          </Form.Group>

          <Form.Group>
            <Form.Control
              type="text"
              placeholder="Entrez une arme"
              value={weapon}
              onChange={(e) => setWeapon(e.target.value)}
            />
          </Form.Group>

          <Button variant="primary" onClick={handleCheckHypothesis}>
            Vérifier l'hypothèse
          </Button>
        </Form>

        <p className="mt-3">{result}</p>
      </div>
    </Container>
  );
}

export default App;
