// src/Board.js
import React from 'react';
import './Board.css'; // Assure-toi de créer ce fichier CSS pour le style

// Exemple de données de pièces avec des occupants fictifs
const defaultRoomsState = [
  { name: 'Cuisine', occupants: [{ name: 'Colonel Moutarde', color: 'jaune' }] },
  { name: 'Grand Salon', occupants: [] },
  { name: 'Petit Salon', occupants: [] },
  { name: 'Salle à manger', occupants: [] },
  { name: 'Bureau', occupants: [] },
  { name: 'Bibliothèque', occupants: [] },
  { name: 'Véranda', occupants: [] },
  { name: 'Hall', occupants: [] },
  { name: 'Studio', occupants: [] }
];

const Board = ({ roomsState = defaultRoomsState }) => {
  return (
    <div className="board">
      {roomsState.length > 0 ? (
        roomsState.map((room, index) => (
          <div key={index} className="room">
            <h3>{room.name}</h3>
            {room.occupants.length > 0 ? (
              room.occupants.map((person, idx) => (
                <p key={idx}>
                  {person.name} ({person.color})
                </p>
              ))
            ) : (
              <p>Aucun occupant</p>
            )}
          </div>
        ))
      ) : (
        <p>Le plateau est vide.</p>
      )}
    </div>
  );
};

export default Board;
