import React from 'react';
import './Board.css'; 

// Dictionnaire des images associées à chaque salle
const roomImages = {
  'Cuisine': '/img/cuisine.jpg',
  'Grand Salon': '/img/grand_salon.jpg',
  'Véranda': '/img/veranda.jpg',
  'Petit Salon': '/img/petit_salon.jpg',
  'Studio': '/img/studio.jpg',
  'Hall': '/img/hall.jpg',
  'Bibliothèque': '/img/bibliotheque.jpg',
  'Salle à manger': '/img/salle_a_manger.jpg',
  'Bureau': '/img/bureau.jpg',
};

const Board = ({ roomsState = [] }) => {
  return (
    <div className="board">
      {roomsState.length > 0 ? (
        roomsState.map((room, index) => (
          <div key={index} className="room">
            <h3>{room.name}</h3>
            <img 
              src={roomImages[room.name]} // Supprimer la gestion de 'default.jpg'
              alt={room.name} 
              className="room-image" 
              onError={(e) => {
                e.target.onerror = null; // Empêche une boucle d'erreur
                e.target.src = ''; // Pas d'image par défaut
              }}
            />
          </div>
        ))
      ) : (
        <p>Le plateau est vide.</p>
      )}
    </div>
  );
};

export default Board;
