// src/App.js
import React, { useState } from 'react';
import { checkHypothesis } from './gameLogic';
import Board from './Board';
import './App.css'; // Assure-toi de créer ce fichier CSS pour le style

function App() {
  const [suspect, setSuspect] = useState('');
  const [weapon, setWeapon] = useState('');
  const [room, setRoom] = useState('');
  const [result, setResult] = useState('');
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [pawns, setPawns] = useState({
    'Colonel Moutarde': 'Cuisine',
    'Docteur Olive': 'Grand Salon',
    // Ajouter les autres pions ici
  });

  const handleCheckHypothesis = () => {
    checkHypothesis(suspect, weapon, room).then(isCorrect => {
      setResult(isCorrect ? "Hypothèse correcte !" : "Hypothèse incorrecte.");
    });
  };

  const handleMovePawn = (pawn) => {
    if (selectedRoom) {
      setPawns(prevPawns => ({ ...prevPawns, [pawn]: selectedRoom }));
      setSelectedRoom(null); // Réinitialiser la sélection de la pièce après le déplacement
    }
  };

  return (
    <div className="app">
      <h1>Cluedo Game</h1>
      <div className="inputs">
        <input
          type="text"
          placeholder="Suspect"
          value={suspect}
          onChange={(e) => setSuspect(e.target.value)}
        />
        <input
          type="text"
          placeholder="Arme"
          value={weapon}
          onChange={(e) => setWeapon(e.target.value)}
        />
        <input
          type="text"
          placeholder="Salle"
          value={room}
          onChange={(e) => setRoom(e.target.value)}
        />
        <button onClick={handleCheckHypothesis}>Vérifier l'hypothèse</button>
        <p>{result}</p>
      </div>
      <Board onSelectRoom={setSelectedRoom} />
      <div className="pawns">
        {Object.keys(pawns).map(pawn => (
          <div key={pawn} className="pawn">
            <span>{pawn}</span>
            <button onClick={() => handleMovePawn(pawn)}>Déplacer vers {selectedRoom || '...'}</button>
            <p>Actuellement dans: {pawns[pawn]}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
