import React, { useState, useEffect } from 'react'; 
import { Container, Button, Form } from 'react-bootstrap';
import { checkHypothesis, getInitialState } from './gameLogic';
import './App.css';
import Board from './Board';

function App() {
  const [suspect, setSuspect] = useState('');
  const [weapon, setWeapon] = useState('');
  const [result, setResult] = useState('');
  const [roomsState, setRoomsState] = useState([]);
  const [playerPosition, setPlayerPosition] = useState(''); // Salle actuelle du joueur
  const [crimeRoom, setCrimeRoom] = useState(''); // Salle du crime
  const [aiPositions, setAiPositions] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const initializePositions = (rooms) => {
    const allRooms = rooms;
    const randomPlayerRoom = getRandomRoom(allRooms);
    const availableRooms = allRooms.filter(room => room !== randomPlayerRoom);

    const ai1Room = getRandomRoom(availableRooms);
    const ai2Room = getRandomRoom(availableRooms.filter(r => r !== ai1Room));
    const ai3Room = getRandomRoom(availableRooms.filter(r => r !== ai1Room && r !== ai2Room));

    const initialAiPositions = [
      { name: 'IA 1', room: ai1Room },
      { name: 'IA 2', room: ai2Room },
      { name: 'IA 3', room: ai3Room }
    ];

    setPlayerPosition(randomPlayerRoom);
    setAiPositions(initialAiPositions);

    // Initialisez la salle du crime au hasard
    const randomCrimeRoom = getRandomRoom(allRooms);
    setCrimeRoom(randomCrimeRoom);
  };

  const getRandomRoom = (rooms) => {
    return rooms[Math.floor(Math.random() * rooms.length)];
  };

  useEffect(() => {
    const initializeGame = async () => {
      const initialState = await getInitialState();
      if (initialState) {
        const rooms = Object.values(initialState);
        setRoomsState(rooms.map(room => ({ name: room })));
        initializePositions(rooms);
      }
      setIsLoading(false);
    };

    initializeGame();
  }, []);

  const handleMovePlayer = (newRoom) => {
    setPlayerPosition(newRoom);
    console.log('Player moved to:', newRoom); // Pour vérifier que la position est bien mise à jour
    moveAI();
  };

  const moveAI = () => {
    const updatedAiPositions = aiPositions.map(ai => {
      const accessibleRooms = getAccessibleRooms(ai.room);
      return {
        ...ai,
        room: accessibleRooms.length > 0 ? getRandomRoom(accessibleRooms) : ai.room
      };
    });
    setAiPositions(updatedAiPositions);
  };

  const getAccessibleRooms = (currentRoom) => {
    const roomMap = {
      'Cuisine': ['Grand Salon', 'Salle à manger'],
      'Grand Salon': ['Cuisine', 'Véranda', 'Salle à manger'],
      'Véranda': ['Grand Salon', 'Petit Salon'],
      'Petit Salon': ['Véranda', 'Hall', 'Bibliothèque'],
      'Studio': ['Hall'],
      'Hall': ['Cuisine', 'Grand Salon', 'Petit Salon', 'Studio', 'Bureau'],
      'Bibliothèque': ['Petit Salon'],
      'Salle à manger': ['Grand Salon'],
      'Bureau': ['Hall']
    };
    return roomMap[currentRoom] || [];
  };

  const handleCheckHypothesis = () => {
    console.log("Checking hypothesis:", { suspect, weapon, room: playerPosition });

    const result = checkHypothesis(suspect, weapon, playerPosition, () => {
      // Fonction onGameOver
      console.log("Game Over!");
      // Ajoutez ici la logique pour terminer le jeu
    });

    // Log supplémentaire pour vérifier les valeurs comparées
    console.log("Player is in room:", playerPosition);
    console.log("Crime happened in:", crimeRoom);

    let message = '';
    if (result.suspect) message += "Suspect correct! ";
    else message += "Suspect incorrect. ";
    if (result.weapon) message += "Arme correcte! ";
    else message += "Arme incorrecte. ";
    if (playerPosition === crimeRoom) {
      message += "Pièce correcte! ";
    } else {
      message += "Pièce incorrecte. ";
    }

    if (result.isCorrect && playerPosition === crimeRoom) {
      message += "Vous avez gagné !";
      // La fonction onGameOver sera appelée automatiquement si nécessaire
    }

    setResult(message);
    alert(message);
  };

  if (isLoading) {
    return <div>Chargement...</div>; // Afficher un message de chargement
  }

  return (
    <Container style={{ position: 'relative', display: 'flex' }}>
      <div style={{ flexGrow: 1 }}>
        <h1 className="text-center my-4">Cluedo Game</h1>
        <Board 
          roomsState={roomsState} 
          playerPosition={playerPosition} 
          setPlayerPosition={handleMovePlayer} 
          aiPositions={aiPositions}
        />

        <Form>
          <Form.Group>
            <Form.Control
              type="text"
              placeholder="Entrez un suspect"
              value={suspect}
              onChange={(e) => setSuspect(e.target.value)}
            />
          </Form.Group>

          <Form.Group>
            <Form.Control
              type="text"
              placeholder="Entrez une arme"
              value={weapon}
              onChange={(e) => setWeapon(e.target.value)}
            />
          </Form.Group>

          <Button variant="primary" onClick={handleCheckHypothesis}>
            Vérifier l'hypothèse
          </Button>
        </Form>

        <p className="mt-3">{result}</p>
      </div>
    </Container>
  );
}

export default App;
