// src/Board.js
import React from 'react';
import './Board.css'; // Assure-toi que le fichier CSS existe bien

// Dictionnaire des images associées à chaque salle
const roomImages = {
  'Cuisine': '/img/cuisine.jpg',
  'Grand Salon': '/img/grand_salon.jpg',
  'Véranda': '/img/veranda.jpg',
  'Petit Salon': '/img/petit_salon.jpg',
  'Studio': '/img/studio.jpg',
  'Hall': '/img/hall.jpg',
  'Bibliothèque': '/img/bibliotheque.jpg',
  'Salle à manger': '/img/salle_a_manger.jpg',
  'Bureau': '/img/bureau.jpg',
};

// Composant Board qui affiche les salles et leurs images
const Board = ({ roomsState = [] }) => {
  return (
    <div className="board">
      {roomsState.length > 0 ? (
        roomsState.map((room, index) => (
          <div key={index} className="room">
            <h3>{room.name}</h3>
            <img
              src={roomImages[room.name] || '/img/default.jpg'} 
              alt={room.name}
              className="room-image"
              onError={(e) => {
                e.target.onerror = null; // Empêche la boucle d'erreur
                e.target.src = '/img/default.jpg'; // Image par défaut si non trouvée
              }}
            />
          </div>
        ))
      ) : (
        <p>Pas de salles disponibles.</p>
      )}
    </div>
  );
};

export default Board;
