import React from 'react';
import './Board.css'; // Assure-toi de créer ce fichier CSS pour le style

// Import des illustrations des salles
import cuisineImage from './assets/cuisine.jpg';
import salonImage from './assets/salon.jpg';
import verandaImage from './assets/veranda.jpg';
// Ajoute les autres images de la même manière...

// Correspondance entre les salles et les images
const roomImages = {
  'Cuisine': cuisineImage,
  'Grand Salon': salonImage,
  'Véranda': verandaImage,
  // Ajoute les autres correspondances ici...
};

// Composant Board
const Board = ({ roomsState = [] }) => {
  return (
    <div className="board">
      {roomsState.length > 0 ? (
        roomsState.map((room, index) => (
          <div key={index} className="room">
            <h3>{room.name}</h3>
            <img 
              src={roomImages[room.name] || 'default.jpg'} 
              alt={room.name} 
              className="room-image" 
            />
            {room.occupants.length > 0 ? (
              room.occupants.map((person, idx) => (
                <p key={idx}>
                  {person.name} ({person.color})
                </p>
              ))
            ) : (
              <p>Aucun occupant</p>
            )}
          </div>
        ))
      ) : (
        <p>Le plateau est vide.</p>
      )}
    </div>
  );
};

export default Board;
